// frontend/src/App.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
    const [status, setStatus] = useState(null);

    useEffect(() => {
        // Realiza una solicitud GET a la nueva ruta en el servidor
        axios.get('http://localhost:5000/status')
            .then(response => {
                setStatus(response.data.status);
            })
            .catch(error => {
                console.error('Error al realizar la solicitud:', error.message);
            });

    }, []); // El segundo argumento del useEffect vacío asegura que la solicitud se realice solo una vez

    return (
        <div className="App">
            {/* Contenido de tu aplicación */}
            {status && <p>{status}</p>}
        </div>
    );
}

export default App;
